PREFIX tragsa: <http://ontologia/tragsa#>  
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>  
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  

SELECT ?workunit ?label (MAX (?geom) AS ?geom) WHERE { 
  ?workunit rdf:type tragsa:Municipality ;  
  rdfs:label ?label ; 
  geo:hasGeometry ?g 
 OPTIONAL{?g geo:asWKT ?geom}
