PREFIX geo: <http://www.opengis.net/ont/geosparql#> 

SELECT ?workunit ?label (MAX (?geom) AS ?geom) WHERE { 
  ?workunit  rdf:type tragsa:Neighborhood ; 
  rdfs:label ?label ; 
  tragsa:hasPluviometry ?o ; 
  tragsa:hasAverageTemperature ?p ; 
  tragsa:hasMinimumTemperature ?q ; 
  tragsa:hasMaximumTemperature ?r ; 
  geo:hasGeometry ?g . 
  OPTIONAL {?g geo:asWKT ?geom} 
  FILTER(((?o = <http://www.tragsa.es/resources/1000Pluvio>) || 
    (?o = <http://www.tragsa.es/resources/1200Pluvio>) || (?o = <http://www.tragsa.es/resources/1400Pluvio>) || 
    (?o = <http://www.tragsa.es/resources/1600Pluvio>)) && ((?p = <http://www.tragsa.es/resources/12AvTemp>)) && 
    ((?q = <http://www.tragsa.es/resources/6MinTemp>) || (?q = <http://www.tragsa.es/resources/7MinTemp>)) && 
    ((?r = <http://www.tragsa.es/resources/22MaxTemp>) || (?r = <http://www.tragsa.es/resources/24MaxTemp>) || 
    (?r = <http://www.tragsa.es/resources/26MaxTemp>) || (?r = <http://www.tragsa.es/resources/28MaxTemp>) || 
    (?r = <http://www.tragsa.es/resources/30MaxTemp>) || (?r = <http://www.tragsa.es/resources/32MaxTemp>))) 
}ORDER BY ?label

