  tragsa:hasRunoff ?o ; 
  rdfs:label ?label ; 
  geo:hasGeometry ?g . 
  OPTIONAL {?g geo:asWKT ?geom}
  FILTER (((?o = <http://www.tragsa.es/resources/100Runoff>) || (?o = <http://www.tragsa.es/resources/150Runoff>) || 
    (?o = <http://www.tragsa.es/resources/200Runoff>)) && ((?p = <http://www.tragsa.es/resources/110Slope>) || 
