  FILTER((?o = <http://www.tragsa.es/resources/300Runoff>) || (?o = <http://www.tragsa.es/resources/400Runoff>) || 
    (?o = <http://www.tragsa.es/resources/500Runoff>) || (?o = <http://www.tragsa.es/resources/600Runoff>) || 
