SELECT * WHERE { 
  ?s rdf:type tragsa:AnimalSpecies ; 
  rdfs:label ?o 
}GROUP BY ?s ORDER BY ?o

PREFIX geo: <http://www.opengis.net/ont/geosparql#> 
PREFIX tragsa: <http://ontologia/tragsa#> 

SELECT * WHERE { 
  { <http://www.tragsa.es/resources/41TreeSp> tragsa:isPrimaryTreeSpeciesOf ?o} 
  UNION                                     
  { <http://www.tragsa.es/resources/41TreeSp> tragsa:isSecondaryTreeSpeciesOf ?o} 
  UNION 
  { <http://www.tragsa.es/resources/41TreeSp> tragsa:isTertiaryTreeSpeciesOf ?o} 
  OPTIONAL {?o tragsa:isForestryTileOf ?p . 
    ?p rdf:type tragsa:Neighborhood ; 
    geo:hasGeometry ?q . 
    ?q geo:asWKT ?geom}
}GROUP BY ?geom
